SELECT * FROM sys.dm_external_data_processed;

SELECT * FROM sys.configurations
WHERE name LIKE 'Data Processed %';

sp_set_data_processed_limit
    @type = N'monthly', -- 'N' in T-SQL stands for National Language or Unicode. It indicates that the string is a Unicode string literal.
    @limit_to = 2

sp_set_data_processed_limit
    @type = N'weekly', -- 'N' in T-SQL stands for National Language or Unicode. It indicates that the string is a Unicode string literal.
    @limit_to = 1

sp_set_data_processed_limit
    @type = N'daily', -- 'N' in T-SQL stands for National Language or Unicode. It indicates that the string is a Unicode string literal.
    @limit_to = 1
