USE nyc_taxi_ldw
GO

CREATE OR ALTER PROCEDURE silver.usp_silver_trip_data_green
@year VARCHAR(4),
@month VARCHAR(2)
AS
BEGIN

    DECLARE @create_sql_stmt NVARCHAR(MAX),
            @drop_sql_stmt   NVARCHAR(MAX);

    SET @create_sql_stmt = 
        'CREATE EXTERNAL TABLE silver.trip_data_green_' + @year + '_' + @month + 
        ' WITH
            (
                DATA_SOURCE = nyc_taxi_src,
                LOCATION = ''silver/trip_data_green/year=' + @year + '/month=' + @month + ''',
                FILE_FORMAT = parquet_file_format
            )
        AS
        SELECT *
        FROM bronze.vw_trip_data
        WHERE year = ''' + @year + '''
          AND month = ''' + @month + '''';    

    print(@create_sql_stmt)

    EXEC sp_executesql @create_sql_stmt;

    SET @drop_sql_stmt = 
        'DROP EXTERNAL TABLE silver.trip_data_green_' + @year + '_' + @month;

    print(@drop_sql_stmt)
    EXEC sp_executesql @drop_sql_stmt;

END;

USE nyc_taxi_ldw;

IF NOT EXISTS(SELECT * FROM sys.external_data_sources WHERE name = 'nyc_taxi_src')
CREATE EXTERNAL DATA SOURCE nyc_taxi_src
WITH (
    LOCATION = 'https://adlssynnyctaxi.dfs.core.windows.net/nyc-taxi-data'
);

SELECT [name],[location] FROM sys.external_data_sources;

SELECT * FROM sys.dm_external_data_processed;

SELECT * FROM sys.configurations
WHERE name LIKE 'Data Processed %';

sp_set_data_processed_limit
    @type = N'monthly', -- 'N' in T-SQL stands for National Language or Unicode. It indicates that the string is a Unicode string literal.
    @limit_to = 2

